/*
* 给定一个字符串，请将字符串里的字符按照出现的频率降序排列。(桶排序)
* */
public class TimesChar {
    public String frequencySort(String s) {
        Map<Character,Integer> map=new HashMap<>();
        for (char c:s.toCharArray())
            map.put(c,map.getOrDefault(c,0)+1);
        ArrayList<Character>[] al=new ArrayList[s.length()+1];
        for (Character c:map.keySet()){
            int counts=map.get(c);
            if (al[counts]==null)al[counts]=new ArrayList<>();
            al[counts].add(c);
        }
        StringBuffer result=new StringBuffer();
        for (int i=al.length-1;i>=0;i--){
            if (al[i]!=null){
                for (char c:al[i]){
                    for (int j=0;j<i;j++)
                        result.append(c);
                }
            }
        }
        return result.toString();
    }

    public String frequencySort2(String s)//运行时间过长
    {
        Map<Character,Integer> map=new HashMap<>();
        for (char c:s.toCharArray())
            map.put(c,map.getOrDefault(c,0)+1);
        ArrayList<String>[] al=new ArrayList[s.length()+1];
        for (Character c:map.keySet()){
            int counts=map.get(c);
            String ss="";
            for (int i=0;i<counts;i++)
                ss+=c;
            if (al[counts]==null)al[counts]=new ArrayList<>();
            al[counts].add(ss);
        }
        StringBuffer result=new StringBuffer();
        for (int i=al.length-1;i>=0;i--){
            if (al[i]!=null){
                int c=al[i].size()-1;
                while (al[i].size()!=0)
                    result.append(al[i].remove(c--));
            }
        }
        return result.toString();
    }

    public static void main(String[] args) {

        String str="";
        long start1=System.currentTimeMillis();
        new TimesChar().frequencySort(str);
        long end1=System.currentTimeMillis();
        long start=System.currentTimeMillis();
        new TimesChar().frequencySort2(str);
        long end=System.currentTimeMillis();
        System.out.println(end1-start1);
        System.out.println(end-start);
    }
}
